import React, { useState } from 'react';
import './ExpenseForm.css';

const ExpenseForm = () => {
    let [title, setTitle] = useState('');
    const titleChangeHandler = (event) => {
        setTitle(event.target.value);
    };
    
  return (
    <form>
      <div className='new-expensecontrols'>
        <div>
          <label className='new-expensecontrol label'>Title</label>
          <input type='text' onChange={titleChangeHandler} className='new-expensecontrol input' />
        </div>
        <div>
          <label className='new-expensecontrol label'>Amount</label>
          <input type='number' min='0.01' step='' className='new-expensecontrol input' />
        </div>
        <div>
          <label className='new-expensecontrol label'>Date</label>
          <input type='date' min='2019-01-01' max='2022-12-31' className='new-expensecontrol input' />
        </div>
      </div>
      <div className='new-expenseactions'>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
