import React, { useState } from 'react';
import './ExpenseForm.css';

const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');

    const titleChangeHandler = (event) => {
        setEnteredTitle(event.target.value);
    };
    const amountChangeHandler = (event) => {
        setEnteredAmount(event.target.value);
    };
    const dateChangeHandler = (event) => {
        setEnteredDate(event.target.value);
    };
    const submitHandler = (event) => {
        event.preventDefault();
        const expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        };
        console.log(expenseData);
    }

  return (
    <form onSubmit={submitHandler}>
      <div className='new-expensecontrols'>
        <div>
          <label className='new-expensecontrol label'>Title</label>
          <input type='text' onChange={titleChangeHandler} className='new-expensecontrol input' />
        </div>
        <div>
          <label className='new-expensecontrol label'>Amount</label>
          <input type='number' min='0.01' step='0.01' onChange={amountChangeHandler} className='new-expensecontrol input' />
        </div>
        <div>
          <label className='new-expensecontrol label'>Date</label>
          <input type='date' min='2019-01-01' max='2022-12-31' onChange={dateChangeHandler} className='new-expensecontrol input' />
        </div>
      </div>
      <div className='new-expenseactions'>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
